sourcesContent:
import crypto from 'crypto';
import fs from 'fs';
import mimeType from 'mime-types';

function hash_password(pass) {
  const hash = crypto.createHash('sha1');
  hash.update(pass);
  return hash.digest('hex');
}

function generate_uuid() {
  return crypto.randomUUID();
}

function createFile(dir, filename, content) {
  fs.mkdir(dir, { recursive: true }, (err) => {
    if (err) {
      console.log(`Error creating the folder: ${err}`);
      return false;
    } else {
      console.log('Folder created');
    }
  });
  return fs.writeFile(`${dir}/${filename}`, content, (err) => {
    if (err) {
      console.log(`Error: Saving file ${err}`);
      return false;
    } else {
      console.log(`File succesfully created at ${dir}/${filename}`);
      return true;
    }
  });
}

function checkFileExist(path) {
  console.log(path);
  if (fs.accessSync(path)) {
    return true;
  } else {
    return false;
  }
}

function getMimeType(filename) {
  return mimeType.lookup(filename);
}

module.exports = { hash_password, generate_uuid, createFile, checkFileExist, getMimeType };

