import redisClient from '../utils/redis';
import dbClient from '../utils/db';
import { generate_uuid, createFile, checkFileExist, getMimeType } from '../utils/helper_func';

async function postUpload(req, res) {
  const token = req.headers['x-token'];
  const id = await redisClient.get(`auth_${token}`);
  const user = await dbClient.getUserById(id);
  if (user === null) {
    res.status(401).send({ 'error': 'Unauthorized' });
  } else {
    const name = req.body.name;
    const type = req.body.type;
    const parentId = req.body.parentId || 0;
    const isPublic = req.body.isPublic || false;
    const data = req.body.data;
    if (name === null) {
      res.status(400).send({ 'error': 'Missing name' });
    } else if (type === null || !['folder', 'file', 'image'].includes(type)) {
      res.status(400).send({ 'error': 'Missing type' });
    } else if (data === null && type !== 'folder') {
      res.status(400).send({ 'error': 'Missing data' });
    } else {
      if (parentId !== 0) {
        const parent = await dbClient.getFileById(parentId);
        if (parent == null) {
          res.status(400).send({ 'error': 'Parent not found' });
        } else if (parent.type !== 'folder') {
          res.status(400).send({ 'error': 'Parent is not a folder' });
        }
      } else {
        const file = {
          name: name,
          type: type,
          parentId: parentId,
          isPublic: isPublic,
          userId: user.id,
        };
        let new_file = null;
        if (type == 'folder') {
          new_file = await dbClient.newFile(file);
        } else {
          const PATH = process.env.FOLDER_PATH || '/tmp/files_manager';
          const filename = generate_uuid();
          const content = atob(data);
          createFile(PATH, filename, content);
          file.localPath = `${PATH}/${filename}`;
          new_file = await dbClient.newFile(file);
        }
        res.status(201).send(new_file);
      }
    }
  }
}

async function getShow(req, res) {
  const token = req.headers['x-token'];
  const userID = await redisClient.get(`auth_${token}`);
  const user = await dbClient.getUserById(userID);
  if (user === null) {
    res.status(401).send({ 'error': 'Unauthorized' });
  } else {
    const fileID = req.params.id;
    const file = await dbClient.getFileById(fileID);
    if (file === null) {
      res.status(404).send({ 'error': 'Not found' });
    } else {
      res.status(200).send(file);
    }
  }
}

async function getIndex(req, res) {
  const NB_ITEMS = 20;
  const token = req.headers['x-token'];
  const userID = await redisClient.get(`auth_${token}`);
  const user = await dbClient.getUserById(userID);
  if (user === null) {
    res.status(401).send({ 'error': 'Unauthorized' });
  } else {
    const parentId = req.query.parentId || 0;
    const page = parseInt(req.query.page, 10) || 0;
    /**
     * @todo: Find the list of files
     */
    const files = [parentId, page];
    res.status(200).send(files);
  }
}

async function putPublish(req, res) {
  const token = req.headers['x-token'];
  const userID = await redisClient.get(`auth_${token}`);
  const user = await dbClient.getUserById(userID);
  if (user === null) {
    res.status(401).send({ 'error': 'Unauthorized' });
  } else {
    const fileID = req.params.id;
    let file = await dbClient.getFileById(fileID);
    if (file === null) {
      res.status(404).send({ 'error': 'Not found' });
    } else {
      file.isPublic = true;
      file = await dbClient.updateFile(file);
      res.status(200).send(file);
    }
  }
}

async function putUnpublish(req, res) {
  const token = req.headers['x-token'];
  const userID = await redisClient.get(`auth_${token}`);
  const user = await dbClient.getUserById(userID);
  if (user === null) {
    res.status(401).send({ 'error': 'Unauthorized' });
  } else {
    const fileID = req.params.id;
    let file = await dbClient.getFileById(fileID);
    if (file === null) {
      res.status(404).send({ 'error': 'Not found' });
    } else {
      file.isPublic = false;
      file = await dbClient.updateFile(file);
      res.status(200).send(file);
    }
  }
}

async function getFile(req, res) {
  const fileID = req.params.id;
  const file = await dbClient.getFileById(fileID);
  if (file === null) {
    res.status(404).send({ 'error': 'Not found' });
  } else {
    const token = req.headers['x-token'];
    const userID = await redisClient.get(`auth_${token}`);
    const user = await dbClient.getUserById(userID);
    if (!file.isPublic && user === null) {
      res.status(404).send({ 'error': 'Not found' });
    } else if (file.type === 'folder') {
      res.status(404).send({ 'error': 'A folder doesn\'t have content' });
    } else {
      if (checkFileExist(file.localPath)) {
        const mimeType = getMimeType(file.name);
        res.set('Content-Type', mimeType);
        res.send(file.localPath);
      } else {
        res.status(404).send({ 'error': 'Not found' });
      }
    }
  }
}

module.exports = { postUpload, getShow, getIndex, putPublish, putUnpublish, getFile };

